document.addEventListener('DOMContentLoaded',()=>{document.querySelectorAll('pre > code').forEach((codeBlock,index)=>{const pre=codeBlock.parentElement;const wrapper=document.createElement('div');wrapper.className='code-wrapper';wrapper.style.position='relative';pre.parentNode.insertBefore(wrapper,pre);wrapper.appendChild(pre);const button=document.createElement('button');button.className='copy-btn';button.innerText='Copy';button.setAttribute('data-code-id',index);button.addEventListener('click',()=>{const text=codeBlock.innerText;navigator.clipboard.writeText(text).then(()=>{button.innerText='Copied!';setTimeout(()=>{button.innerText='Copy';},2000);}).catch(()=>{button.innerText='Failed';});});button.style.position='absolute';button.style.top='8px';button.style.right='8px';wrapper.appendChild(button);});});